Gftt_Response.codeGenerator:
function code(inputs, outputs, node, generator) {
  const code = `${outputs[1]} = K.feature.gftt_response(${inputs[1]}['value'], ${inputs.slice(2).join(', ')})
${outputs[1]} = {
  'value': ${outputs[1]},
  'dataType': 'torch.tensor',
  'metadata': {
    'colorChannel': 'rgb' if ${inputs[1]}['metadata']['colorChannel'] == 'rgb' else 'grayscale',
    'channelOrder': 'channelFirst',
    'isMiniBatched': True,
    'intensityRange': '0-1',
    'device': 'cpu' if ${inputs[1]}['value'].get_device() == -1 else 'gpu'
  }
}
${outputs[0]}`;
  return code;
}

